import React, { useEffect, useState } from 'react'
import { useHttp } from '../hooks/http.hooks'
import modalInfo from '../modules/toast/main'
import './UploadPage.css'



function UploadPage() {
    const { loading, error, request, clearError } = useHttp()

    const [selectedFiles, setSelectedFiles] = useState([])

    const selectFile = (event) => {
        console.log(event.target.files)
        setSelectedFiles(event.target.files)
    }

    useEffect(() => {
        modalInfo(error)
        clearError(null)

        var control = document.getElementById("your-files");
        control.addEventListener("change", function (event) {
            // Когда происходит изменение элементов управления, значит появились новые файлы
            var i = 0,
                files = control.files,
                len = files.length;

            for (; i < len; i++) {
                console.log("Filename: " + files[i].name);
                console.log("Type: " + files[i].type);
                console.log("Size: " + files[i].size + " bytes");
            }

        }, false);

    }, [error, clearError])


    async function uploadSubmit(e) {
        e.preventDefault()
        // console.log(selectedFiles[0])
        const info = {
            name: selectedFiles[0].name,
            lastModified: selectedFiles[0].lastModified,
            size: selectedFiles[0].size,
            type: selectedFiles[0].type
        }
        try {
            const data = await request('/api/file/test', 'POST', info, { 'Content-Type': 'multipart/form-data' })
            console.log(data)
        } catch (error) { }
    }


    return (
        <form onSubmit={uploadSubmit} id="upload-container" >
            <input type="file" id="your-files" multiple />
            <button type="submit" name="file" disabled={loading}>Обработать</button>

        </form>


    )
}

export default UploadPage
