import React, { useState } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'




function AuthPage() {
    const [authSignIn, setAuthSignIn] = useState(false)

    const [valueEmail, setValueEmail] = useState('')
    const [valuePassword, setValuePassword] = useState('')
    const [valueLogin, setValueLogin] = useState('')
    const [valueCheck, setValueCheck] = useState(true)

    const [buttonSingUp, setButtonSignUp] = useState(false)
    const [buttonSingIn, setButtonSignIn] = useState(false)

    // console.log(valueCheck)




    function formValidatonSignUp() {

        if (valueCheck && valueEmail.length > 4 && valuePassword.length > 6 && valueLogin.length > 4) {

            setButtonSignUp(true)
        }
    }
    function formValidatonSignIn() {
        console.log(valuePassword.length)
        if (valueEmail.length > 1 && valuePassword.length > 1) {
            setButtonSignIn(true)
        }
    }

    // console.log(authSignIn);
    return (
        <div className="row justify-content-center align-items-center" style={{ marginTop: "10rem" }}>
            <div className="col-4 " style={{ backgrounddColor: "#857686" }}>

                {authSignIn ?
                    <div className="row  switcher-auth text-center">
                        <div className="col-6 " onClick={setAuthSignIn.bind(null, false)}>Sign Up</div>
                        <div className="col-6 switcher-auth__active" onClick={setAuthSignIn.bind(null, true)}>Sign In</div>
                    </div>
                    :
                    <div className="row  switcher-auth text-center">
                        <div className="col-6 switcher-auth__active" onClick={setAuthSignIn.bind(null, false)}>Sign Up</div>
                        <div className="col-6 " onClick={setAuthSignIn.bind(null, true)}>Sign In</div>
                    </div>
                }




                {authSignIn ?

                    <form className="mt-4">
                        <div className="mb-3">
                            <label htmlFor="Email" className="form-label">Email address</label>
                            <input
                                type="email"
                                className="form-control"
                                id="Email"
                                aria-describedby="emailHelp"
                                value={valueEmail}
                                onChange={
                                    (e) => {
                                        setValueEmail(e.target.value)
                                        formValidatonSignIn()
                                    }
                                }
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="Password" className="form-label" aria-describedby="passwordHelpBlock">Password</label>
                            <input
                                type="password"
                                className="form-control"
                                id="Password"
                                value={valuePassword}
                                onChange={
                                    (e) => {
                                        setValuePassword(e.target.value)
                                        formValidatonSignIn()
                                    }
                                }
                            />
                        </div>

                        {buttonSingIn ?
                        <button type="submit" className="btn btn-outline-success btn-block" >Exit</button>
                        :
                        <button type="submit" className="btn btn-outline-success btn-block" disabled >Exit</button>
                        }
                        
                    </form>

                    :

                    <form className="mt-4">
                        <div className="mb-3">
                            <label htmlFor="Email" className="form-label">Email address</label>
                            <input
                                type="email"
                                className="form-control"
                                id="Email"
                                aria-describedby="emailHelp"
                                value={valueEmail}
                                onChange={
                                    (e) => {
                                        setValueEmail(e.target.value)
                                        formValidatonSignUp()
                                    }
                                }
                            />
                            <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                        </div>
                        <div className="mb-3">
                            <label htmlFor="Login" className="form-label">Login</label>
                            <input
                                type="text"
                                className="form-control"
                                id="Login"
                                value={valueLogin}
                                onChange={
                                    (e) => {
                                        setValueLogin(e.target.value)
                                        formValidatonSignUp()
                                    }
                                }
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="Password" className="form-label" aria-describedby="passwordHelpBlock">Password</label>
                            <input
                                type="password"
                                className="form-control"
                                id="Password"
                                value={valuePassword}
                                onChange={
                                    (e) => {
                                        setValuePassword(e.target.value)
                                        formValidatonSignUp()
                                    }
                                }
                            />
                        </div>
                        <div className="mb-3 form-check">
                            <input
                                type="checkbox"
                                className="form-check-input"
                                id="CheckRuls"
                                checked={valueCheck}
                                onChange={
                                    (e) => {
                                        setValueCheck(e.target.checked)
                                        // console.log(valueCheck);
                                        formValidatonSignUp()
                                    }
                                }
                            />
                            <label className="form-check-label" htmlFor="CheckRuls">Check rules</label>
                        </div>

                        {buttonSingUp ?
                        <button type="submit" className="btn btn-outline-success btn-block" >Register</button>
                        :
                        <button type="submit" className="btn btn-outline-success btn-block" disabled >Register</button>
                        }

                    </form>

                }

            </div>

        </div>
    )
}

export default AuthPage
