import React, { useEffect, useState } from 'react'
import { useHttp } from '../hooks/http.hooks'
import modalInfo from '../modules/toast/main'
import './UploadPage.css'



function UploadPage() {
    const { loading, error, request, clearError } = useHttp()

    const [selectedFiles, setSelectedFiles] = useState(undefined)

    const selectFile = (event) => {
        console.log(event.target.files)
        setSelectedFiles(event.target.files)
    }

    useEffect(() => {
        modalInfo(error)
        clearError(null)
    }, [error, clearError])
 

    async function uploadSubmit(e) {
        e.preventDefault()
        try {
            const data = await request('/api/file/upload', 'POST', selectedFiles[0], { 'Content-Type': 'multipart/form-data' })
            console.log(data)
        } catch (error) { }
    }


    return (
        <form onSubmit={uploadSubmit}  id="upload-container" >
            <script src="https://code.jquery.com/jquery-3.5.1.slim.js"></script>
            <img id="upload-image" src="https://habrastorage.org/webt/dr/qg/cs/drqgcsoh1mosho2swyk3kk_mtwi.png" />
            <div>
                <input id="file-input" type="file" name="file" multiple onChange={selectFile}/>
                <label htmlFor="file-input">Выберите файл</label>
                <span>или перетащите его сюда</span>
                {/* <input type="file" id="getFiles" multiple  /> */}
            </div>
            <button type="submit" name="file" disabled={loading}>Обработать</button>
        </form>

    )
}

export default UploadPage
