function uploadPage(e) {

    const fileInput = document.querySelector('#file-input')
    const uploadContainer= document.querySelector('#upload-container')

    let dropZone = $('#upload-container');

    console.log(fileInput.nextSibling)

    uploadContainer.addEventListener('hover', ()=> console.log(this))
    // (()=>(
    //     console.log(11)
    // ))
    
    uploadContainer.addEventListener("focus", () => {
        console.log(11)
        fileInput.nextSibling.classList.add('focus');
    })

    uploadContainer.addEventListener("blur", () => {
        fileInput.nextSibling.classList.remove('focus');
    })
}


export default uploadPage
