function modalInfo(text) {
    console.log(text, 'это тест')

    if (text) {

        let div = document.createElement('div')
        div.className = 'toast-top-left'
        div.innerHTML = `
            <div>
                ${text}
            </div>
        `
        const block = document.body.append(div)

        setTimeout(() => {
            div.remove()
        }, 1500);
    }



}


export default modalInfo
