        //             authorization: `Bearer ${token}`
        //         })
        //         setLink(file)
        //         file = undefined
        //         // console.log(file)
        //     } catch (error) { }
        // }

        // useCallback(()=>{
        //     getLink()
        // }, [getLink()])

        getLink()

    }, [token, linkId, request])

    if (loading) {
        return <Loader />
    }

    return (
        <>
            {!loading && link && <FileCard file={link} />}

            {
            listM.map((item, idx) => {
                return (
                    <Post item={item} key={idx}></Post>)
            })
            }
        </>
    )
}

export default DetailPage
