function modalInfo(text) {
    console.log(text, 'это тест')

    
  let div = document.createElement('div');
  div.className = "alert";
  div.innerHTML = "<strong>Всем привет!</strong> Вы прочитали важное сообщение.";

  document.body.append(div);

}


export default modalInfo
