import React, { useEffect, useState } from 'react'
import * as B from 'bootstrap'
import { useHttp } from '../hooks/http.hooks'

import { Tooltip, Toast, Popover } from 'bootstrap'




function AuthPage() {
    const bootstrap = require('bootstrap')
    const { loading, error, request } = useHttp()

    const [authSignIn, setAuthSignIn] = useState(false)

    const [valueEmail, setValueEmail] = useState('')
    const [valuePassword, setValuePassword] = useState('')
    const [valueLogin, setValueLogin] = useState('')
    const [valueCheck, setValueCheck] = useState(true)

    const [buttonSingUp, setButtonSignUp] = useState(false)
    const [buttonSingIn, setButtonSignIn] = useState(false)

    useEffect(() => {
        // console.log(2)

    }, [error])

    function formValidatonSignUp() {
        if (valueCheck && valueEmail.length > 4 && valuePassword.length > 6 && valueLogin.length > 4) {
            setButtonSignUp(true)
        } else {
            setButtonSignUp(false)
        }
    }
    function formValidatonSignIn() {
        if (valueEmail.length > 1 && valuePassword.length > 1) {
            setButtonSignIn(true)
        } else {
            setButtonSignIn(false)
        }
    }


    const registerHandler = async () => {
        try {
            const send = { email: valueEmail, login: valueLogin, password: valuePassword }
            const data = await request('/api/auth/register', 'POST', send)
            console.log(data)
        } catch (error) { }
    }

    return (
        <div className="row justify-content-center align-items-center" style={{ marginTop: "10rem" }}>
            <div className="col-4 ">

                {authSignIn ?
                    <div className="row  switcher-auth text-center">
                        <div className="col-6 " onClick={setAuthSignIn.bind(null, false)}>Sign Up</div>
                        <div className="col-6 switcher-auth__active" onClick={setAuthSignIn.bind(null, true)}>Sign In</div>
                    </div>
                    :
                    <div className="row  switcher-auth text-center">
                        <div className="col-6 switcher-auth__active" onClick={setAuthSignIn.bind(null, false)}>Sign Up</div>
                        <div className="col-6 " onClick={setAuthSignIn.bind(null, true)}>Sign In</div>
                    </div>
                }




                {authSignIn ?

                    <form className="mt-4">
                        <div className="mb-3">
                            <label htmlFor="Email" className="form-label">Email address</label>
                            <input
                                type="email"
                                className="form-control"
                                id="Email"
                                aria-describedby="emailHelp"
                                value={valueEmail}
                                onChange={
                                    (e) => {
                                        setValueEmail(e.target.value)
                                        formValidatonSignIn()
                                    }
                                }
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="Password" className="form-label" aria-describedby="passwordHelpBlock">Password</label>
                            <input
                                type="password"
                                className="form-control"
                                id="Password"
                                value={valuePassword}
                                onChange={
                                    (e) => {
                                        setValuePassword(e.target.value)
                                        formValidatonSignIn()
                                    }
                                }
                            />
                        </div>

                        {buttonSingIn ?
                            <button type="submit" className="btn bg-success btn-block text-white" >Exit</button>
                            :
                            <button type="submit" className="btn btn-block text-white bg-danger bg-gradient" disabled >Exit</button>
                        }

                    </form>

                    :

                    <form className="mt-4">
                        <div className="mb-3">
                            <label htmlFor="Email" className="form-label">Email address</label>
                            <input
                                type="email"
                                className="form-control"
                                id="Email"
                                aria-describedby="emailHelp"
                                value={valueEmail}
                                onChange={
                                    (e) => {
                                        setValueEmail(e.target.value)
                                        formValidatonSignUp()
                                    }
                                }
                            />
                            <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                        </div>
                        <div className="mb-3">
                            <label htmlFor="Login" className="form-label">Login</label>
                            <input
                                type="text"
                                className="form-control"
                                id="Login"
                                value={valueLogin}
                                onChange={
                                    (e) => {
                                        setValueLogin(e.target.value)
                                        formValidatonSignUp()
                                    }
                                }
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="Password" className="form-label" aria-describedby="passwordHelpBlock">Password</label>
                            <input
                                type="password"
                                className="form-control"
                                id="Password"
                                value={valuePassword}
                                onChange={
                                    (e) => {
                                        setValuePassword(e.target.value)
                                        formValidatonSignUp()
                                    }
                                }
                            />
                        </div>
                        <div className="mb-3 form-check">
                            <input
                                type="checkbox"
                                className="form-check-input"
                                id="CheckRuls"
                                checked={valueCheck}
                                onChange={
                                    (e) => {
                                        setValueCheck(e.target.checked)
                                        formValidatonSignUp()
                                    }
                                }
                            />
                            <label className="form-check-label" htmlFor="CheckRuls">Check rules</label>
                        </div>

                        {buttonSingUp ?
                            <div className="btn bg-success btn-block text-white" onClick={registerHandler} disabled={loading} >Register</div>
                            :
                            <button type="submit" className="btn btn-block text-white bg-danger bg-gradient" disabled >Register</button>
                        }

                    </form>

                }

            </div>




            <div aria-live="polite" aria-atomic="true" style={{ position: "relative", minHeight: "200px" }}>
                <div className="toast" style={{ position: "absolute", top: 0, right: 0 }}>
                    <div className="toast-header">
                        <img src="..." className="rounded mr-2" alt="..." />
                        <strong className="mr-auto">Bootstrap</strong>
                        <small>11 mins ago</small>
                        <button type="button" className="ml-2 mb-1 close" data-dismiss="toast" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <div className="toast-body">
                        Hello, world! This is a toast message.
                    </div>
                </div>
            </div>

        </div>
    )
}

export default AuthPage
