function uploadPage(e) {

    const fileInput = document.querySelector('#file-input')

    console.log(fileInput)

    fileInput.focus(() => {
        fileInput.nextSibling.addClass('focus');
    })
        .focusout(function () {
            fileInput.nextSibling.removeClass('focus');
        });
}


export default uploadPage
