import React, { useEffect, useState } from 'react'
import { useHttp } from '../hooks/http.hooks'
import modalInfo from '../modules/toast/main'


function UploadPage() {
    const { loading, error, request, clearError } = useHttp()

    const [selectedFiles, setSelectedFiles] = useState(undefined)
  
    const [fileInfos, setFileInfos] = useState([])

    const selectFile = (event) => {
        setSelectedFiles(event.target.files)
    }



    useEffect(() => {
        modalInfo(error)
        // setTimeout(() => {
        //     clearError(null)
        // }, 1500)
        clearError(null)
        // console.log(error)
    }, [error, clearError])


    async function uploadSubmit(e) {
        e.preventDefault()
        try {
            const header = { 'Content-Type': 'multipart/form-data' }
            const body = {
                title: 'zzzzz',
                idx: '222324'
            }
            const data = await request('/api/file/upload', 'POST', body, header)
            console.log(data)
            // auth.login(data.token, data.userId)
        } catch (error) { }
    }


    return (
        <form onSubmit={uploadSubmit}>
            <label>Файл</label><br />
            <input type="file" id="getFiles" multiple />
            <button type="submit" name="file" disabled={loading} onChange={this.onChange}>Обработать</button>
        </form>
    )
}

export default UploadPage
