import React, { useEffect, useState } from 'react'
import { useAuth } from '../hooks/auth.hooks'
import { useHttp } from '../hooks/http.hooks'
import modalInfo from '../modules/toast/main'
import './UploadPage.css'


function UploadPage() {
    const { error, request, clearError } = useHttp()
    const [image, setImage] = useState('')
    const [loading, setLoading] = useState(false)
    const [file, setFile] = useState(null)

    const { token, login, logout, userId } = useAuth()

    const uploadImage = async e => {
        e.preventDefault()
        console.log(e.target.elements.buffer)
        const files = e.target.elements.buffer.file
        const data = new FormData()
        data.append('file', files[0])
        data.append('upload_preset', 'darwin')
        // setLoading(true)
        console.log(files)

        console.log(data)
        // // const res = await fetch('api/file/upload', { method: 'POST', body: data })
        // // const res = await request('api/file/upload', 'POST', data, {token})
        const res = await fetch('api/file/test', { method: 'POST', body: data, headers: { 'enctype': 'multipart/form-data' } })




        modalInfo(res)

    }
    useEffect(() => {

    }, [])

    return (
        <div className="App">
            {/* <input name="file" type="file"
                class="file-upload" data-cloudinary-field="image_id"
                data-form-data="{ 'transformation': {'crop':'limit','tags':'samples','width':3000,'height':2000}}" />
            <hr /><hr /><hr /><hr /> */}
            {/* <h1>Upload Image</h1>
            <input
                type="file"
                name="file"
                placeholder="Upload an image"
                onChange={uploadImage}
            />
            {loading ? (
                <h3>Loading...</h3>
            ) : (
                    <img src={image} style={{ width: '300px' }} />
                )} */}

            <form onSubmit={uploadImage}>
                <label>Файл</label><br />
                <input type="file" name="buffer" multiple /><br /><br />
                <input type="submit" value="Send" height="30px" />
            </form>
        </div>
    )
}
export default UploadPage
